script:
frappe.ui.form.on('Quotation', {
    // Trigger on refresh
    refresh(frm) {
        update_custom_header_title(frm);
    },

    // Trigger on field changes
    custom_function_date: function(frm) {
        update_custom_header_title(frm);
    },
    custom_function_time: function(frm) {
        update_custom_header_title(frm);
    },
    custom_people_count: function(frm) {
        update_custom_header_title(frm);
    }
});

function update_custom_header_title(frm) {
    if (frm.doc.custom_function_date && frm.doc.custom_function_time && frm.doc.custom_people_count) {

         // Convert the date to a day of the week
        let date = new Date(frm.doc.custom_function_date);
        let tamil_day = get_tamil_day_name(date.getDay());

        // Convert time to 12-hour format and determine period of day
        let time_parts = frm.doc.custom_function_time.split(':');
        let hours = parseInt(time_parts[0], 10);
        let minutes = time_parts[1];

        let period_of_day = '';
        let meridian_time = '';

        // Determine the period of day and convert to 12-hour format
        if (hours < 12) {
            if (hours < 10) {
                period_of_day = 'காலை';  // Early Morning
            } else if (hours >= 10 && hours < 17) {
                period_of_day = 'மதியம்';  // Afternoon
            }
            meridian_time = period_of_day +' ' + (hours === 0 ? 12 : hours) + ':' + minutes + 'மணி ';
        } else {
            if (hours < 20) {
                period_of_day = 'மாலை';  // Evening
            } else if (hours >= 20 && hours < 24) {
                period_of_day = 'இரவு';  // Night
            }
            meridian_time = period_of_day+ ' ' + (hours === 12 ? 12 : hours - 12) + ':' + minutes +' '+ 'மணி ';
        }

        // Constructing the custom header title with the formatted time and period of day
        frm.set_value('custom_header_title', 
            frm.doc.custom_function_date + ' ' +
            tamil_day+' '+
            meridian_time + ' - ' +
            frm.doc.custom_people_count + ' பேர்'
        );
    }
}

// Function to get the Tamil day name
function get_tamil_day_name(day) {
    let tamil_days = ['ஞாயிறு', 'திங்கள்', 'செவ்வாய்', 'புதன்', 'வியாழன்', 'வெள்ளி', 'சனி'];
    return tamil_days[day];
}
